import React, {Component} from 'react';
import { Button, TextField, Checkbox, FormControlLabel, Link, ThemeProvider, createMuiTheme} from '@material-ui/core';
import { Link as Lr } from 'react-router-dom';
import LoginPageService from './LoginPageService';
/* import './styles.css'; */

const theme = createMuiTheme({
    overrides: {
        MuiTypography:{
            colorPrimary:{
                color: '#0073FC'
            }
        },
        MuiInputBase:{
            input:{
                backgroundColor: 'FFFFFF',
                border: '1px solid #CCCCCC',
                borderRadius: '4px',
            },
            root:{
                marginBottom: '16px',
                fontFamily: 'Wonder Unit Sans',
                width: '100%'
            }
        },
        MuiFormControl:{
            root:{  
                alignItems: 'center',
                width: '75%'
            }
        }
    }
})


class LoginPage extends Component {
    constructor(props){
        super(props);
        const email = localStorage.getItem('email');
        if(email !== null){
            this.state={
                email: email,
                password: '',
                loading: false,
                checked: true,
                error: '',
            }
        }else{
            this.state={
                email: '',
                password: '',
                loading: false,
                checked: false,
                error: '',
            }
        }
    }

    submit = async (e) => {
        e.preventDefault();
        const { email, password, checked } = this.state;
        this.setState({ loading: true });
        try {
            if( email !== '' && password !== ''){

                const body = { email: email, password: password}
    
                const res = await LoginPageService.attemptLogin(body);
                console.log(res);
                if(res === true){
                    if(checked){
                        localStorage.setItem('email', email);
                    }
                    this.setState({ loading: false });
                    /* return window.location.reload(); */
                }else{
                  
                    this.setState({ error: 'Credenciais Invalidas'})
                    this.setState({ loading: false });
                    return false;
                }
            }
        } catch (error) {
            this.setState({ error: 'Credenciais Invalidas'})
            this.setState({ loading: false });
            throw error;
        }
    }

    changeEmail = (e) => {
        const value = e.target.value;
        this.setState({ email: value });
    }

    changePassword = (e) => {
        const value = e.target.value;
        this.setState({ password: value });
    }

    checkboxChange = (e) => {
        const { checked } = this.state;
        if(!checked){
            localStorage.setItem('checked', true);
            this.setState({ checked: true });
        }else{
            localStorage.removeItem('email');
            localStorage.setItem('checked', false);
            this.setState({ checked: false });
        }
    }

