    // .MuiTypography-colorPrimary
    render(){
        const { password, email, loading, checked } = this.state;

        return(
            <ThemeProvider theme={theme}>
                <div className="color-background">
                    <div className="box">
                        <div className=" div-img vertical-aling">
                           {/*  <img className='img' src={require('../..//assets/images/icons/img-background-login.png')} alt="img" /> */}
                        </div>
                        <div className="box-card">
                            <div className="card-login " variant="outlined">
                                <div className="logo-form-login">
                                    {/* <img className=" logo-form__img text-center" src={require('../../assets/images/icons/logo-form.png')} alt="img"/> */}
                                    <p>App</p>
                                </div>
                                <form onSubmit={this.submit}>
                                    <div className='centerForm'>
                                        <TextField
                                            required={true}
                                            variant="outlined"
                                            size='small'
                                            type='email'
                                            placeholder="exemplo@exemplo.com"
                                            onChange={this.changeEmail}
                                            value={email}
                                            onSubmit={this.submit}
                                        />
                                        <TextField
                                            required={true}
                                            variant="outlined"
                                            size='small'
                                            type='password'
                                            placeholder="Password"
                                            onChange={this.changePassword}
                                            value={password}
                                            onSubmit={this.submit}
                                        />
                                    </div>
                                    <div className='checkbox'>
                                        <FormControlLabel
                                            label='Lembrar-me'
                                            control={
                                                <Checkbox
                                                    color='primary'
                                                    name='remember'
                                                    value={checked}
                                                    checked={checked}
                                                    onChange={this.checkboxChange}
                                                />
                                            }
                                                
                                        />
                                        <Link component={Lr} className='link' to='/forgot'>
                                                Esqueci minha senha
                                        </Link>
                                    </div>
                                    <div className='centerForm'>
                                        <Button
                                            disabled={loading}
                                            type='submit'
                                            variant='contained'
                                            color='primary'
                                            className='btn-login'
                                        >
                                            Entrar
                                        </Button>
                                    </div>
                                    {/* {rt} */}
                                    {this.state.error}
                                </form>

                            </div>
                            <p className="direitos"> Â© 2020. Todos os direitos reservados </p>
                        </div>
                    </div>    
                </div>     
            </ThemeProvider>            
        );
    }
}

export default LoginPage;
